import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function Chatbot({ affectedRegions = [] }) {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: 'Hello! I\'m your AI health assistant. I can help answer general health questions and provide information about symptoms. How can I help you today?'
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const messagesEndRef = useRef(null);
  const recognitionRef = useRef(null);

  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInput(transcript);
        setIsListening(false);
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    }
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const toggleVoiceInput = () => {
    if (!recognitionRef.current) {
      alert('Speech recognition is not supported in your browser. Please use Chrome, Safari, or Edge.');
      return;
    }

    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMessage = input.trim();
    setInput('');

    const newMessages = [...messages, { role: 'user', content: userMessage }];
    setMessages(newMessages);
    setIsLoading(true);

    try {
      const response = await fetch('/api/chatbot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userMessage,
          affectedRegions: affectedRegions,
          history: messages.slice(-10)
        }),
      });

      const data = await response.json();

      if (data.ok) {
        setMessages([...newMessages, { role: 'assistant', content: data.response }]);
      } else {
        setMessages([...newMessages, { 
          role: 'assistant', 
          content: 'I apologize, but I encountered an error. Please try again or consult with a healthcare professional.' 
        }]);
      }
    } catch (error) {
      console.error('Chatbot error:', error);
      setMessages([...newMessages, { 
        role: 'assistant', 
        content: 'I apologize, but I\'m having trouble connecting right now. Please try again later.' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <button
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Open chatbot"
        className="fixed bottom-5 right-5 z-40 h-14 w-14 rounded-full bg-gradient-to-br from-brand-500 to-sky-600 text-white shadow-xl hover:scale-105 active:scale-95 transition grid place-items-center"
      >
        <AnimatePresence mode="wait">
          {isOpen ? (
            <motion.svg
              key="close"
              initial={{ rotate: -90, opacity: 0 }}
              animate={{ rotate: 0, opacity: 1 }}
              exit={{ rotate: 90, opacity: 0 }}
              transition={{ duration: 0.2 }}
              viewBox="0 0 24 24"
              fill="none"
              className="h-6 w-6"
            >
              <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
            </motion.svg>
          ) : (
            <motion.svg
              key="chat"
              initial={{ rotate: -90, opacity: 0 }}
              animate={{ rotate: 0, opacity: 1 }}
              exit={{ rotate: 90, opacity: 0 }}
              transition={{ duration: 0.2 }}
              viewBox="0 0 24 24"
              fill="none"
              className="h-6 w-6"
            >
              <path d="M21 12a8 8 0 1 1-3.78-6.78L21 5l-.78 3.78A8 8 0 0 1 21 12Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M8 13h5M8 9h8" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
            </motion.svg>
          )}
        </AnimatePresence>
      </button>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 20, scale: 0.95 }}
            transition={{ duration: 0.2 }}
            className="fixed bottom-24 right-5 z-40 w-96 max-w-[calc(100vw-2.5rem)] h-[600px] max-h-[calc(100vh-8rem)] bg-white dark:bg-slate-800 rounded-2xl shadow-2xl border border-slate-200 dark:border-slate-700 flex flex-col overflow-hidden"
          >
            <div className="bg-gradient-to-br from-brand-500 to-sky-600 text-white p-4 flex items-center justify-between">
              <div>
                <h3 className="font-semibold text-lg">Health Assistant</h3>
                <p className="text-xs text-white/80">AI-powered guidance</p>
              </div>
              <button
                onClick={() => setIsOpen(false)}
                className="text-white/80 hover:text-white transition"
                aria-label="Close chatbot"
              >
                <svg viewBox="0 0 24 24" fill="none" className="h-5 w-5">
                  <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
                </svg>
              </button>
            </div>

            <div className="bg-yellow-50 dark:bg-yellow-900/20 border-b border-yellow-200 dark:border-yellow-800 px-4 py-2">
              <p className="text-xs text-yellow-800 dark:text-yellow-200">
                ‚ö†Ô∏è <strong>Disclaimer:</strong> This AI may provide inaccurate information. Always consult a healthcare professional for medical advice.
              </p>
            </div>

            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {messages.map((msg, idx) => (
                <div
                  key={idx}
                  className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-[80%] rounded-2xl px-4 py-2 ${
                      msg.role === 'user'
                        ? 'bg-brand-500 text-white'
                        : 'bg-slate-100 dark:bg-slate-700 text-slate-900 dark:text-slate-100'
                    }`}
                  >
                    <p className="text-sm whitespace-pre-wrap">{msg.content}</p>
                  </div>
                </div>
              ))}

              {isLoading && (
                <div className="flex justify-start">
                  <div className="bg-slate-100 dark:bg-slate-700 rounded-2xl px-4 py-3">
                    <div className="flex space-x-2">
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                    </div>
                  </div>
                </div>
              )}

              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={sendMessage} className="border-t border-slate-200 dark:border-slate-700 p-4">
              <div className="flex gap-2">
                <button
                  type="button"
                  onClick={toggleVoiceInput}
                  className={`p-2 rounded-lg transition ${
                    isListening
                      ? 'bg-red-500 text-white'
                      : 'bg-slate-100 dark:bg-slate-700 text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-600'
                  }`}
                  aria-label={isListening ? 'Stop listening' : 'Start voice input'}
                  title={isListening ? 'Stop listening' : 'Voice input'}
                >
                  <svg viewBox="0 0 24 24" fill="none" className="h-5 w-5">
                    {isListening ? (
                      <path d="M19 10v2a7 7 0 01-7 7v0a7 7 0 01-7-7v-2M12 19v4M8 23h8M12 1a3 3 0 013 3v8a3 3 0 01-6 0V4a3 3 0 013-3z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    ) : (
                      <path d="M12 1a3 3 0 013 3v8a3 3 0 01-6 0V4a3 3 0 013-3zM19 10v2a7 7 0 01-14 0v-2M12 19v4M8 23h8" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    )}
                  </svg>
                </button>

                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Ask about symptoms..."
                  className="flex-1 px-4 py-2 rounded-lg border border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-700 text-slate-900 dark:text-slate-100 placeholder-slate-400 dark:placeholder-slate-500 focus:outline-none focus:ring-2 focus:ring-brand-500"
                  disabled={isLoading}
                />

                <button
                  type="submit"
                  disabled={!input.trim() || isLoading}
                  className="px-4 py-2 bg-gradient-to-br from-brand-500 to-sky-600 text-white rounded-lg hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed transition"
                  aria-label="Send message"
                >
                  <svg viewBox="0 0 24 24" fill="none" className="h-5 w-5">
                    <path d="M22 2L11 13M22 2l-7 20-4-9-9-4 20-7z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                </button>
              </div>

              {isListening && (
                <p className="text-xs text-red-600 dark:text-red-400 mt-2 flex items-center gap-1">
                  <span className="inline-block w-2 h-2 bg-red-500 rounded-full animate-pulse"></span>
                  Listening... Speak now
                </p>
              )}
            </form>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

backend/app/routes/face_recognition.py.content:
from flask import Blueprint, request, jsonify
import cv2
import numpy as np
import base64
import json
import os
from pathlib import Path
from datetime import datetime

bp = Blueprint("face_recognition", __name__)

FACE_DATA_DIR = 'backend/face_data'
os.makedirs(FACE_DATA_DIR, exist_ok=True)

CASCADE_PATH = str(Path(__file__).resolve().parent.parent / 'cascade_data' / 'haarcascade_frontalface_default.xml')
face_cascade = cv2.CascadeClassifier(CASCADE_PATH)

if face_cascade.empty():
    raise RuntimeError(f"Failed to load Haar cascade from {CASCADE_PATH}. File may be missing or corrupted.")

def decode_base64_image(base64_string):
    if ',' in base64_string:
        base64_string = base64_string.split(',')[1]

    img_data = base64.b64decode(base64_string)
    nparr = np.frombuffer(img_data, np.uint8)
    img = cv2.imdecode(nparr, cv2.IMREAD_COLOR)
    return img

def detect_face(img):
    gray = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)
    faces = face_cascade.detectMultiScale(gray, 1.3, 5, minSize=(100, 100))

    if len(faces) == 0:
        return None

    x, y, w, h = faces[0]
    face_roi = img[y:y+h, x:x+w]
    face_resized = cv2.resize(face_roi, (128, 128))

    return face_resized

def compute_face_histogram(face):
    hist_b = cv2.calcHist([face], [0], None, [256], [0, 256])
    hist_g = cv2.calcHist([face], [1], None, [256], [0, 256])
    hist_r = cv2.calcHist([face], [2], None, [256], [0, 256])

    hist_b = cv2.normalize(hist_b, hist_b).flatten()
    hist_g = cv2.normalize(hist_g, hist_g).flatten()
    hist_r = cv2.normalize(hist_r, hist_r).flatten()

    return np.concatenate([hist_b, hist_g, hist_r])

def compare_faces(hist1, hist2):
    correlation = cv2.compareHist(hist1.reshape(-1, 1), hist2.reshape(-1, 1), cv2.HISTCMP_CORREL)
    return correlation

@bp.route('/face/register', methods=['POST'])
def register_face():
    try:
        data = request.json
        mobile = data.get('mobile')
        image_data = data.get('image')
        user_name = data.get('userName', 'User')

        if not mobile or not image_data:
            return jsonify({'success': False, 'message': 'Mobile and image are required'}), 400

        img = decode_base64_image(image_data)
        if img is None:
            return jsonify({'success': False, 'message': 'Invalid image data'}), 400

        face = detect_face(img)
        if face is None:
            return jsonify({'success': False, 'message': 'No face detected. Please ensure your face is clearly visible.'}), 400

        face_histogram = compute_face_histogram(face)

        user_data = {
            'mobile': mobile,
            'userName': user_name,
            'faceHistogram': face_histogram.tolist(),
            'registeredAt': datetime.now().isoformat()
        }

        file_path = os.path.join(FACE_DATA_DIR, f'{mobile}.json')
        with open(file_path, 'w') as f:
            json.dump(user_data, f)

        face_image_path = os.path.join(FACE_DATA_DIR, f'{mobile}_face.jpg')
        cv2.imwrite(face_image_path, face)

        return jsonify({
            'success': True,
            'message': 'Face registered successfully',
            'userName': user_name
        })

    except Exception as e:
        print(f"Error in register_face: {str(e)}")
        return jsonify({'success': False, 'message': f'Error: {str(e)}'}), 500

@bp.route('/face/login', methods=['POST'])
def login_face():
    try:
        data = request.json
        image_data = data.get('image')

        if not image_data:
            return jsonify({'success': False, 'message': 'Image is required'}), 400

        img = decode_base64_image(image_data)
        if img is None:
            return jsonify({'success': False, 'message': 'Invalid image data'}), 400

        face = detect_face(img)
        if face is None:
            return jsonify({'success': False, 'message': 'No face detected. Please ensure your face is clearly visible.'}), 400

        current_histogram = compute_face_histogram(face)

        best_match = None
        best_score = -1
        threshold = 0.75

        for filename in os.listdir(FACE_DATA_DIR):
            if filename.endswith('.json'):
                file_path = os.path.join(FACE_DATA_DIR, filename)
                with open(file_path, 'r') as f:
                    user_data = json.load(f)

                stored_histogram = np.array(user_data['faceHistogram'])
                score = compare_faces(current_histogram, stored_histogram)

                if score > best_score:
                    best_score = score
                    best_match = user_data

        if best_match and best_score >= threshold:
            return jsonify({
                'success': True,
                'message': 'Face recognized successfully',
                'mobile': best_match['mobile'],
                'userName': best_match['userName'],
                'confidence': float(best_score)
            })
        else:
            return jsonify({
                'success': False,
                'message': 'Face not recognized. Please try again or use another login method.',
                'confidence': float(best_score) if best_score > 0 else 0
            }), 401

    except Exception as e:
        print(f"Error in login_face: {str(e)}")
        return jsonify({'success': False, 'message': f'Error: {str(e)}'}), 500

@bp.route('/face/health', methods=['GET'])
def health():
    return jsonify({'status': 'ok', 'message': 'Face recognition API is running'})
